import React from 'react';
import TestUtils from 'react-addons-test-utils';
import expect from 'expect';
import expectJSX from 'expect-jsx';

expect.extend(expectJSX);

import LikeCounter from './like-counter';

describe('LikeCounter', () => {
  let  renderer;
  beforeEach(()=>{
    renderer = TestUtils.createRenderer();
  });
  it('should have active class', () => {
    renderer.render(<LikeCounter count={5} isActive={true} />);
    expect(renderer.getRenderOutput().props.className).toContain('LikeCounter--active');
  });
  it('should not have active class', () => {
    renderer.render(<LikeCounter count={5} isActive={false} />);
    expect(renderer.getRenderOutput().props.className).toNotContain('LikeCounter--active');
  });
  it('should render multiple like counts', () => {
    renderer.render(<LikeCounter count={19} />)
    expect(renderer.getRenderOutput()).toIncludeJSX('19 likes');
  });
  it('should render single like count', () => {
    renderer.render(<LikeCounter count={1} />)
    expect(renderer.getRenderOutput()).toIncludeJSX('1 like');
  });
  it('should not render 0 like counts', () => {
    renderer.render(<LikeCounter count={0} />)
    expect(renderer.getRenderOutput()).toNotIncludeJSX('0 like');
  });
});

