import expect from 'expect';
import Store from '../stores/app-store';
import AppConstants from '../constants/app-constants';
import AppActions from '../actions/app-actions';

describe('AppStore', () => {

  beforeEach(() => {
    Store.resetState();
  });

  it('should add a quote', () => {
    AppActions.addQuote({
      text: 'The best way to cheer yourself up is to try and cheer somebody else up.',
      author: 'Mark Twain'
    });

    expect(Store.getState().quotes.length).toEqual(5);
    expect(Store.getState().quotes[4]).toEqual({
      'id': 4,
      'quote':{'author':'Mark Twain', 'text':'The best way to cheer yourself up is to try and cheer somebody else up.'},
      'likeCount': 0
    });
  });

  it('should remove a quote', () => {
    AppActions.removeQuote({ id: 0 });

    expect(Store.getState().quotes.length).toEqual(3);
    expect(Store.getState().quotes[0]).toEqual({
      'id': 1,
      'quote':{'author':'David', 'text':'I like the number 1'},
      'likeCount': 0
    });
  });

  it('should like a quote', () => {
    AppActions.likeQuote({ id: 2 });
    expect(Store.getState().quotes[2].likeCount).toEqual(1);
  });

  it('should unlike a quote', () => {
    AppActions.likeQuote({ id: 2 });
    AppActions.likeQuote({ id: 2 });
    AppActions.likeQuote({ id: 2 });
    AppActions.unlikeQuote({ id: 2 });

    expect(Store.getState().quotes[2].likeCount).toEqual(2);
  });

  it('should work with a series of actions', () => {
    let actions = [{
      action:AppActions.addQuote,
      payload:{
        text: 'The best way to cheer yourself up is to try and cheer somebody else up.',
        author: 'Mark Twain'
      }
    },{
      action:AppActions.addQuote,
      payload:{
        text: 'The best way to cheer yourself up is to drink lots.',
        author: 'Mark Twain'
      }
    },{
      action:AppActions.removeQuote,
      payload:{
        id: 5
      }
    },{
      action:AppActions.addQuote,
      payload:{
        text: 'Don\'t worry about your problems with maths. I assure you mine are far greater.',
        author: 'Albert Einstein'
      }
    },{
      action:AppActions.likeQuote,
      payload:{ id: 2 }
    },{
      action:AppActions.likeQuote,
      payload:{ id: 2 }
    },{
      action:AppActions.likeQuote,
      payload:{ id: 2 }
    },{
      action:AppActions.likeQuote,
      payload:{ id: 6 }
    },{
      action:AppActions.unlikeQuote,
      payload:{ id: 2 }
    },{
      action:AppActions.addQuote,
      payload:{
        text: 'E=MC^2',
        author: 'Albert Einstein'
      }
    }];
    actions.forEach(({action, payload}) => action(payload));

    expect(Store.getState().quotes.length).toEqual(7);
    expect(Store.getState().quotes[6]).toEqual({
      'id': 7,
      'quote': {
        'author': 'Albert Einstein', 
        'text': 'E=MC^2'
      },
      'likeCount': 0
    });
    expect(Store.getState().quotes[2].likeCount).toEqual(2);
    expect(Store.getState().quotes[5].likeCount).toEqual(1);
  });
});

