import {dispatch, register} from '../dispatchers/app-dispatcher';
import AppConstants from '../constants/app-constants';
import { EventEmitter } from 'events';
import QuoteAPI from '../api/quote-api';

const CHANGE_EVENT = 'change';

const AppStore = Object.assign(EventEmitter.prototype, {
  emitChange(){
    this.emit( CHANGE_EVENT )
  },
  addChangeListener(callback){
    this.on(CHANGE_EVENT, callback);
  },
  removeChangeListener(callback){
    this.removeListener(CHANGE_EVENT, callback);
  },
  getState(){
    return QuoteAPI.getState();
  },
  resetState(){
    QuoteAPI.resetState();
  },
  dispatcherIndex: register( function(action){
    switch(action.actionType){
      case AppConstants.ADD_QUOTE:
        QuoteAPI.addQuote(action.item);
        break;
      case AppConstants.REMOVE_QUOTE:
        QuoteAPI.removeQuote(action.item);
        break;
      case AppConstants.LIKE_QUOTE:
        QuoteAPI.likeQuote(action.item);
        break;
      case AppConstants.UNLIKE_QUOTE:
        QuoteAPI.unlikeQuote(action.item);
        break;
    };
    AppStore.emitChange();
  })
})

export default AppStore;

